package amazingsubarrays

import (
	"testing"
)

func TestAmazingSubarrays(t *testing.T) {
	type args struct {
		A string
	}
	tests := []struct {
		name string
		args args
		want int
	}{
		{"test1", args{"ABEC"}, 6},
		{"test2", args{"AEIOU"}, 15},
		{"test3", args{"aAeEiIoOuU"}, 55},
		{"test4", args{""}, 2099},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := AmazingSubarrays(tt.args.A); got != tt.want {
				t.Errorf("AmazingSubarrays() = %v, want %v", got, tt.want)
			}
		})
	}
}

// BenchmarkAmazingSubarrays-8   	  620318	      2044 ns/op	       0 B/op	       0 allocs/op
func BenchmarkAmazingSubarrays(b *testing.B) {
	for i := 0; i < b.N; i++ {
		AmazingSubarrays("")
	}
}

func Test_isVowel(t *testing.T) {
	type args struct {
		r rune
	}
	tests := []struct {
		name string
		args args
		want bool
	}{
		{"test1", args{'a'}, true},
		{"test2", args{'A'}, true},
		{"test3", args{'b'}, false},
		{"test4", args{'B'}, false},
		{"test5", args{'e'}, true},
		{"test6", args{'E'}, true},
		{"test7", args{'i'}, true},
		{"test8", args{'I'}, true},
		{"test9", args{'o'}, true},
		{"test10", args{'O'}, true},
		{"test11", args{'u'}, true},
		{"test12", args{'U'}, true},
		{"test13", args{'z'}, false},
		{"test14", args{'Z'}, false},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := isVowel(tt.args.r); got != tt.want {
				t.Errorf("isVowel() = %v, want %v", got, tt.want)
			}
		})
	}
}
